import React,{useState} from 'react'

export default function TextForm(props) {
  const ontime=() =>{
    let t = text.split(" ").length*0.008;
    let k="",m="";
    let f=0;
    for(let i=0;i<t.length;i++){
      if(t[i]=='.'){
        continue;
        f=1;
      }
      else if(f==1){
        k=k+text[i];
      }
      else{
        m=m+text[i];
      }
    }
    let ti=m +":"+k ;
    setTime(ti);
  }
  const handledownclick =() =>{
    console.log("Lowercase was clicked");
    let newtext=text.toLowerCase();
    setText(newtext);
  }
  const handleUpclick =() =>{
    console.log("Uppercase was clicked");
    let newtext=text.toUpperCase();
    setText(newtext);
  }
  const handleOnchange =(event) =>{
    console.log("On change")
    setText(event.target.value);
  }
  const [text, setText] = useState('Enter text here');
  const [time, setTime] = useState('0 : 00 s');
  return (
    <>
        
        <div className="mb-3">
        <h1>{props.heading}</h1>
        <textarea className="form-control" id="myBOX" rows="8" value={text} onChange={handleOnchange}></textarea>
        <button className='btn btn-primary my-3' onClick={handleUpclick}>Convert to uppercase</button>
        <button className='btn btn-primary my-3' onClick={handledownclick}>Convert to Lowercase</button>
        <button className='btn btn-primary my-3' onClick={ontime}>Give Time details</button>
        </div>
        <div className='container my-3'>
        <h2>Your text summary</h2>
        <p>{text.length} characters and {text.split(" ").length} words</p>
        <p>{time} minutes for reading</p>
        </div>
    </>
  )
}
