import React,{useState} from 'react'

export default function TextForm(props) {
  const handledownclick =() =>{
    console.log("Lowercase was clicked");
    let newtext=text.toLowerCase();
    setText(newtext);
  }
  const handlesentenceclick =() =>{
    let t= text.toString();
    for(let i=0;i<t.length;i++){
      if(t[i]=' '){
        t[i+1].toUpperCase();
      }
    }
    setText(t);
  }
  const handleUpclick =() =>{
    console.log("Uppercase was clicked");
    let newtext=text.toUpperCase();
    setText(newtext);
  }
  const handleOnchange =(event) =>{
    console.log("On change")
    setText(event.target.value);
  }
  const handleclrclick =() =>{
    let newtext = '';
    setText(newtext);
  }
  const [text, setText] = useState('Enter text here');
  return (
    <>
        
        <div className="mb-3">
        <h1>{props.heading}</h1>
        <textarea className="form-control" id="myBOX" rows="8" value={text} onChange={handleOnchange}></textarea>
        <button className='btn btn-primary my-3' onClick={handleUpclick}>Convert to uppercase</button>
        <button className='btn btn-primary my-3 mx-3' onClick={handledownclick}>Convert to Lowercase</button>
        <button className='btn btn-primary my-3 ' onClick={handleclrclick}>Clear Text</button>
        <button className='btn btn-primary my-3 ' onClick={handlesentenceclick}>Sentence Case</button>
        </div>
        <div className='container my-3'>
        <h2>Your text summary</h2>
        <p>{text.length} characters and {text.split(" ").length} words</p>
        <p>{text.split(" ").length * 0.008} minutes for reading</p>
        <h3>Preview</h3>
        <p>{text}</p>
        <h3>Bold text</h3>
        <p><b>{text}</b></p>
        <h3>Italic text</h3>
        <p><i>{text}</i></p>
        <h3>Underlined text</h3>
        <p><u>{text}</u></p>
        </div>
    </>
  )
}
